import { useContext, useEffect, useState } from "react";
import Navbar from "../components/Navbar";
import { AuthContext } from "../context/AuthContext";
import axios from "axios";

function Dashboard() {

    const {token} = useContext(AuthContext);
    const [search, setSearch] = useState("");
    const [jobs, setJobs] = useState([]);
    const [filteredJobs, setFilteredJobs] = useState([]);

    const handleSearch=(input)=>{
        setSearch(input);
    }

    useEffect(()=>{
        const token = localStorage.getItem("token");
        if(token){
            fetchjobs();
        }
    },[token]);

    const fetchjobs=async()=>{
        const res= await axios.get("http://localhost:8080/api/jobs",{
            headers:{Authorization: `Bearer ${token}`}
        });
        setJobs(res.data);
    }

    useEffect(()=>{
        let filteredJobs=[...jobs];
        if(search){
            filteredJobs=filteredJobs.filter(job=>job.title.toLowerCase().includes(search.toLowerCase()));
        }
        setFilteredJobs(filteredJobs);
    },[search,jobs]);

    return(
        <div>
            <Navbar onSearch={handleSearch} />
            <div className="user-card"></div>
            <div className="job-container">
            {filteredJobs.map(job=>(
                <div className="job-card" key={job.id}>
                    <h4><span>{job.title}</span></h4>
                    <label>About the job</label>
                    <p>{job.description}</p>
                    <h5>Company: {job.company_name}</h5>
                    <p>Location: {job.location}</p>
                    <label>Key responsibilities:</label>
                    <p>{job.job_type}</p>
                    <p>Salary: {job.salary_range}</p>
                </div>
            ))}
            </div>
        </div>
    );
    
}
export default Dashboard;
