import { useState } from "react";
import axios from "axios";

function Register() {

    const [error, setError] = useState("");
    const [formData, setFormData] = useState({
        role: "",
        fName: "",
        email: "",
        password: "",
        confirmPassword: "",
    });

    const handleChange = (e)=>{
        setFormData((formData)=>(
       { ...formData,
    [e.target.name] : e.target.value})
    );
    }

    const handleSubmit = async(e)=>{
        e.preventDefault();
        setError("");
        if(formData.password !==formData.confirmPassword){
            setError("Password mismatch");
            return;
        }
        try {
            const response= await axios.post("http://localhost:8080/api/register", formData);
            setFormData({
            role: "",
            fName: "",
            email: "",
            password: "",
            confirmPassword: "",
            });
        } catch (error) {
            console.error("Registration failed", error.message);
            setError("Registration failed!");
            
        }

    }


    return(
        <div className="register">
            <h3>Create an account</h3>
            <form onSubmit={handleSubmit}>
                <div>
                <p>Select role: </p>
                <label>
                <input type="radio" name="role" value="merchant" checked={formData.role==="merchant"} onChange={handleChange} />
                Merchant
                </label>
                <label>
                <input type="radio" name="role" value="agent" checked={formData.role==="agent"} onChange={handleChange} />
                Agent
                </label>
                </div>
                <input type="text" name="fName" placeholder="Full name" value={formData.fName} onChange={handleChange} required/>
                <input type="email" name="email" placeholder="Email" value={formData.email} onChange={handleChange} required/>
                <input type="password" name="password" placeholder="Password" value={formData.password} onChange={handleChange} required/>
                <input type="password" name="confirmPassword" placeholder="Confirm password" value={formData.confirmPassword} onChange={handleChange} required/>
                <button type="submit">Submit</button>

            </form>
        </div>
    );
    
}

export default Register;
