import { useContext, useState } from "react";
import { AuthContext } from "../context/AuthContext";
import axios from "axios";


function CvUploads() {
    const {token} = useContext(AuthContext);
    const [user, setUser] = useState();

    const [cv, setCv] = useState(null);

    const handleFileChange=(e)=>{
        setCv(e.target.files[0]);
    }
    const fetchUser =async()=>{
        const token=localStorage.getItem("token");
        const res= await axios.get("http://localhost:8080/api/applicants",{
            headers: {Authorization: `Bearer ${token}`}
        });
        setUser(res.data);
    }
    const handleSubmit=async(e)=>{
        e.preventDefault();

        const formData= new FormData();
        formData.append("cv",cv);
        const token=localStorage.getItem("token");
        try {
            await axios.post("http://localhost:8080/api/upload/upload-cv",formData, {
                headers: {
                    "Content-type": "multipart/form-data",
                    Authorization:`Bearer ${token}`,
                },
            });
            alert("CV uploaded successfully!");
            fetchUser();
        } catch (error) {
          console.error("Upload failed:",error);
          alert("Upload failed");
        }
    }

    return(
    <div>
        <form onSubmit={handleSubmit}>
        <input type="file" name="cv" accept=".pdf,.doc,.docx" onChange={handleFileChange} />
        <button type="submit">Submit</button>
        </form>
    </div>
    );
    
}
export default CvUploads;
